<?phpinclude('../sql.php');function generate_filename() {   $chars = "abcdefghijklmnopqrstuvwxyz0123456789";   $random = "";   for ($i = 0; $i < 32; $i += 1) {      $rand = rand() % strlen($chars);      $random .= substr($chars, $rand, 1);   }   return "$random.jpg";}if ($_POST['eventid']) {   if ($_POST['action'] == "edit") {      $results = mysql_query("select * from events where EventID = '" . $_POST['eventid'] . "'");      $row = mysql_fetch_array($results);   }   else if ($_POST['action'] == "delete") {      $results = mysql_query("select Image from events where EventID = '" . mysql_real_escape_string($_POST['eventid']) . "'");      $del = mysql_fetch_array($results);      unlink("../" . $del['Image']);      mysql_query("delete from events where EventID = '" . mysql_real_escape_string($_POST['eventid']) . "'");   }   else {      if (basename($_FILES['image']['name'])) {         $filename = $_POST['orig_image'];         move_uploaded_file($_FILES['image']['tmp_name'], "../" . $filename);      }      mysql_query("update events set Title = '" . mysql_real_escape_string($_POST['title']) . "', Description = '" . mysql_real_escape_string($_POST['description']) . "', Link = '" . mysql_real_escape_string($_POST['link']) . "' where EventID = '" . mysql_real_escape_string($_POST['eventid']) . "'");   }}else if ($_POST['title']) {   $filename = $_POST['orig_image'];   if (basename($_FILES['image']['name'])) {      $filename = generate_filename();      move_uploaded_file($_FILES['image']['tmp_name'], "../images/events/" . $filename);   }   mysql_query("insert into events (Title, Image, Description, Link, DatePosted) values ('" . mysql_real_escape_string($_POST['title']) . "', 'images/events/" . mysql_real_escape_string($filename) . "', '" . mysql_real_escape_string($_POST['description']) . "', '" . mysql_real_escape_string($_POST['link']) . "', NOW())");}?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://ww